	static ObjectFactory factory = new ObjectFactory();

	private CharakterGenerator() {

	}

	public static Charakter generteNewCharakter(AddNewCharakterDialogResult dialogResult) {
		factory = new ObjectFactory();

		Charakter newCharakter = makeEmptyCharakter();

		Spezies speziesXml = factory.createSpezies();
		Species species = Species.getEnumToName(dialogResult.getSpecies());
		speziesXml.setBasisLeben(species.getBaseLife());
		speziesXml.setBasisGeschwindigkeit(species.getSpeed());
		speziesXml.setBasisSeelenkraft(species.getSpiritualToughnes());
		speziesXml.setBasisZähigkeit(species.getPhysicalToughness());
		speziesXml.setKosten(species.getCost());
		speziesXml.setName(species.getName());
		newCharakter.setSpezies(speziesXml);

		newCharakter.setName(dialogResult.getName());

		return newCharakter;
	}
	
	public static Schablone generateNewSchablone(String name){
		Schablone template;
		try {
			template = MarshallingHelper.getInstance()
					.unmarshallSchablone(EMPTY_TEMPLATE);
		} catch (JAXBException e) {
			e.printStackTrace();
			throw new DataBindingException("something went wrong unmarshalling empty data", e);
		}
		template.setName(name);
		return template;
	}

	private static Charakter makeEmptyCharakter() {
		Charakter emptyCharakter;
		try {
			emptyCharakter = MarshallingHelper.getInstance()
					.unmarshallCharakter(EMPTY_CHARAKTER );
			return emptyCharakter;
		} catch (JAXBException e) {
			e.printStackTrace();
			throw new DataBindingException("something went wrong unmarshalling empty data", e);
		}
	}

